import Link from "next/link";

const BlogLayout = ({ children }) => (
  <div className="container mx-auto md:w-7/12 px-4 py-10 bg-white h-screen overflow-auto">
    {children}
    <div className="fixed border-0 overflow-hidden w-full">
      <Link href="/">
        <a className="inline-block px-4 py-2 bg-gray-300 rounded-sm text-gray-500 fixed text-center">
          <FontAwesomeIcon
            className="fa-1x"
            icon={faArrowLeft}
          ></FontAwesomeIcon>{" "}
        </a>
      </Link>
    </div>
  </div>
);

export default BlogLayout;
