import { useState } from "react";

const ImageUtil = ({ alt, src }) => {
  const image = require("../../content/assets/computer.jpg?trace");
  const [imageLoaded, setImageLoaded] = useState(false);
  if (image.src) {
    setImageLoaded(true);
  }
  return (
    <div className="relative">
      <img
        className={`w-full transition-opacity duration-300 ease-in opacity-100 block"
        }`}
        src={imageLoaded ? image.src : image.trace}
        alt={alt}
      />

      {/* <img
        className={`w-full ${imageLoaded ? " block" : " hidden"}`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      /> */}
    </div>
  );
};

export default ImageUtil;
