// PATH => /posts/<ID_NUMBER>
import Link from "next/link";
import Head from "next/head";
import fs from "fs";
import path from "path";
import matter from "gray-matter";
import ReactMarkdown from "react-markdown/with-html";
import marked from "marked";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

// ICONS
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

export default ({ frontMatter, content }) => (
  <>
    <Head>
      <title>{frontMatter.title}</title>
      <meta name="description" content={frontMatter.description} />
      <meta name="author" content={frontMatter.author}></meta>
    </Head>
    <article>
      <ReactMarkdown escapeHtml={false} source={content} />
      <Link href="/">
        <a className="inline-block px-1 py-2 bg-blue-300 my-2 mx-1 rounded-sm">
          <FontAwesomeIcon
            className="fa-1x"
            icon={faArrowLeft}
          ></FontAwesomeIcon>{" "}
          Go Back
        </a>
      </Link>
    </article>
  </>
);

export async function getStaticPaths() {
  const filesString = fs.readdirSync(path.join("content")).toString();
  const linksArray = filesString.split(",");
  const params = linksArray.map((link) => ({
    params: {
      link: link.replace(".md", ""),
    },
  }));

  return {
    paths: params,
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  // Get File and scrape yaml data
  const post = fs
    .readFileSync(path.join("content", `${params.link}.md`))
    .toString();
  const { data, content } = matter(post);

  // Serialize date
  const dateOptions = { year: "numeric", month: "long", day: "numeric" };
  const date = new Date(data.updatedAt);
  const formattedDate = date.toLocaleDateString("en-US", dateOptions);
  delete data.updatedAt; // delete unnecessary date

  // Make metadata
  const frontMatter = {
    ...data,
    date: formattedDate,
  };

  console.log("Frontmatter", frontMatter);
  return {
    props: {
      content: `#${data.title}\n${content}`,
      frontMatter,
    },
  };
}
