import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);
  console.log(image);
  const variants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
  };
  return (
    <p className="relative">
      <AnimatePresence>
        {imageLoaded ? (
          <motion.img
            initial="hidden"
            animate="visible"
            className={`w-full`}
            src={image.src}
            alt={alt}
          />
        ) : (
          <motion.img
            initial="hidden"
            animate="visible"
            className="w-full"
            src={image.trace}
            alt={alt}
          />
        )}
      </AnimatePresence>

      <img
        className={`absolute top-0 left-0 w-full invisible`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
    </p>
  );
};

export default ImageLoaderUtil;
