import { useState, useEffect } from "react";
import { motion } from "framer-motion";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);
  console.log(image);
  const variants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
  };
  return (
    <p className="relative">
      {imageLoaded === false ? (
        <motion.img
          initial="hidden"
          animate="visible"
          variants={variants}
          className="w-full"
          src={image.trace}
          alt={alt}
        />
      ) : (
        <motion.img
          initial="hidden"
          animate="visible"
          variants={variants}
          className={`w-full`}
          src={image.src}
          alt={alt}
        />
      )}

      <img
        className={`w-full`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
    </p>
  );
};

export default ImageLoaderUtil;
