import { useState } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);

  const visibility = {
    trace: {
      display: "block",
    },
    src: {
      display: "hidden",
    },
  };

  if (imageLoaded) {
    visibility.trace = "hidden";
    visibility.src = "block";
  } else {
    visibility.trace = "block";
    visibility.src = "hidden";
  }

  return (
    <>
      <img
        className={`w-full  ${visibility.src}`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
      <img
        className={`w-full  ${visibility.trace}`}
        src={image.trace}
        alt={alt}
      />
    </>
  );
};

export default ImageLoaderUtil;
