import React, { useRef, useEffect } from "react";
import "lazysizes";
import dynamic from "next/dynamic";
import Markdown from "markdown-to-jsx";
const hljs = require("highlight.js/lib/core");
hljs.registerLanguage(
  "javascript",
  require("highlight.js/lib/languages/javascript"),
);
hljs.registerLanguage("bash", require("highlight.js/lib/languages/bash"));
hljs.registerLanguage("json", require("highlight.js/lib/languages/json"));
hljs.registerLanguage("css", require("highlight.js/lib/languages/css"));
hljs.registerLanguage(
  "html",
  require("highlight.js/lib/languages/vbscript-html"),
);
const ImageLoaderUtil = dynamic(() => import("../Utils/ImageLoaderUtil"));
export function HighlightedMarkdown({ children, metadata }) {
  const rootRef = useRef();

  useEffect(() => {
    rootRef.current.querySelectorAll("pre code").forEach((block) => {
      hljs.highlightBlock(block);
    });
  }, [children]);

  return (
    <div ref={rootRef} className="blog">
      <Markdown
        options={{
          overrides: {
            img: {
              component: ({ alt: altText, src: source }) => {
                const { trace } = require(
                  `../../content/assets/${metadata.link}/${source}?trace`,
                );
                const src = require(
                  `../../content/assets/${metadata.link}/${source}?webp`,
                );
                return (
                  <img
                    alt={altText}
                    src={trace}
                    data-srcset={src}
                    className="lazyload"
                    data-sizes="auto"
                  />
                );
              },
            },
          },
        }}
      >
        {children}
      </Markdown>
    </div>
  );
}
