import { useState } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);

  return (
    <span>
      <a
        className="relative cursor-pointer"
        href={require(`../../content/assets/${src}`)}
        target="blank"
      >
        <div>
          <img
            className={`w-full ${imageLoaded ? " hidden" : " block"}`}
            src={image.trace}
            alt={alt}
          />

          <img
            className={`w-full ${imageLoaded ? " block" : " hidden"}`}
            src={image.src}
            alt={alt}
            loading="lazy"
            onLoad={() => setImageLoaded(true)}
          />
        </div>
      </a>
    </span>
  );
};

export default ImageLoaderUtil;
