import { useState, useEffect } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);

  const [visibility, setVisibility] = useState({
    trace: {
      display: "block",
    },
    src: {
      display: "none",
    },
  });

  useEffect(() => {
    if (imageLoaded) {
      setVisibility({
        trace: {
          display: "none",
        },
        src: {
          display: "block",
        },
      });
    }
  }, [imageLoaded]);

  return (
    <>
      <img
        className={`w-full`}
        src={image.src}
        alt={alt}
        style={visibility.src}
        onLoad={() => {
          console.log(true);
          setImageLoaded(true);
        }}
      />
      <img
        className={`w-full`}
        src={image.trace}
        alt={alt}
        style={visibility.trace}
      />
    </>
  );
};

export default ImageLoaderUtil;
