import { useState } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);

  let visibility = {
    trace: {
      display: "block",
    },
    src: {
      display: "hidden",
    },
  };

  if (imageLoaded) {
    visibility.trace = {
      display: "none",
    };

    visibility.src = {
      display: "block",
    };
  } else {
    {
      visibility.trace = {
        display: "block",
      };

      visibility.src = {
        display: "nonr",
      };
    }
  }

  return (
    <>
      <img
        className={`w-full`}
        src={image.src}
        alt={alt}
        style={visibility.src}
        onLoad={() => setImageLoaded(true)}
      />
      <img
        className={`w-full`}
        src={image.trace}
        alt={alt}
        style={visibility.trace}
      />
    </>
  );
};

export default ImageLoaderUtil;
