import { useState } from "react";

const ImageUtil = ({ alt, src }) => {
  const image = require("../../content/assets/computer.jpg?trace");
  const [imageLoaded, setImageLoaded] = useState(false);

  return (
    <div className="relative w-full">
      <img
        className={`transition-opacity duration-300 ease-in opacity-100 ${
          imageLoaded ? " invisible" : " visible"
        }`}
        src={image.trace}
        alt={alt}
      />

      <img
        className={`${imageLoaded ? " visible" : " invisible"}`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
    </div>
  );
};

export default ImageUtil;
