// MODULES
import { useContext } from "react";
import dynamic from "next/dynamic";
import Link from "next/link";
import { ActiveTabContext } from "../../context/ActiveTab";

// COMPONENTS
const NavigationItems = dynamic(() => import("./NavigationItems"));
const MobileNavigation = dynamic(() => import("./MobileNavigation"));

const Navigation = () => {
  const activeTab = useContext(ActiveTabContext);
  const isIndex = activeTab.tab === "" ? true : false;
  const isBlog = activeTab.tab === "blog" ? true : false;

  return (
    <>
      <nav
        style={{
          background: `${isBlog ? " rgb(99,179,237)" : ""}`,
          background: `${
            isBlog
              ? "linear-gradient(90deg, rgba(99,179,237,1) 0%, rgba(144,205,244,1) 100%)"
              : ""
          }`,
        }}
      >
        <div
          className="flex flex-row justify-between container max-w-screen-md mx-auto px-4 h-24"
        >
          <Link href="/">
            <div className="flex flex-row  items-center">
              {!isIndex ? <div className="heroImg" /> : null}
              <a
                className={`transition-colors duration-300 text-black text-lg tracking-wider cursor-pointer ${
                  isIndex ? "text-white font-medium" : "ml-4"
                }`}
              >
                Melvin George
              </a>
            </div>
          </Link>
          <ul
            className={`${
              isIndex ? "text-white font-medium" : "text-black"
            } text-lg md:block my-auto hidden`}
          >
            <NavigationItems />
          </ul>
          <MobileNavigation isIndex={isIndex} />
        </div>
      </nav>
      <style jsx>
        {`
        .heroImg {
          width: 60px;
          height: 60px;
          margin: 15px auto;
          border-radius: 50%;
          background: url(${require("../../public/me.png?webp")}) -10px / cover
            no-repeat ${isBlog ? "#BEE3F8" : "rgb(241, 241, 241)"};
          background-repeat: no-repeat;
        }
      `}
      </style>
    </>
  );
};

export default Navigation;
