import { useState } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);
  const styles = {
    trace: {
      opacity: 100,
    },
  };
  // Hide preview when image has loaded.
  if (imageLoaded) {
    styles.trace = { opacity: 0 };
  }

  return (
    <div className="relative">
      <img
        className={`w-full transition transition-opacity duration-500 ease-in ${
          imageLoaded ? " hidden" : " block"
        }`}
        src={image.trace}
        alt={alt}
        style={styles.trace}
      />

      <img
        className={`w-full transition transition-opacity duration-500 ease-out ${
          imageLoaded ? " block" : " hidden"
        }`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
    </div>
  );
};

export default ImageLoaderUtil;
