import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);
  console.log(image);
  const variants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
  };
  return (
    <p className="relative">
      <motion.img
        initial="visible"
        animate="visible"
        variants={variants}
        className="w-full"
        src={image.trace}
        alt={alt}
      />
      <AnimatePresence>
        <motion.img
          initial="hidden"
          animate="visible"
          variants={variants}
          className={`w-full top-0 left-0 absolute`}
          src={image.src}
          alt={alt}
          onLoad={() => setImageLoaded(true)}
        />
      </AnimatePresence>
    </p>
  );
};

export default ImageLoaderUtil;
