import { useState } from "react";

const ImageUtil = ({ alt, src }) => {
  const image = require("../../content/assets/computer.jpg?trace");
  const [imageLoaded, setImageLoaded] = useState(false);
  let styles = {
    opacity: 0,
  };
  if (imageLoaded) {
    styles.opacity = 100;
  }
  return (
    <div className="relative">
      <img
        className={`w-full transition-opacity duration-300 ease-in opacity-100 ${
          imageLoaded ? " hidden" : " block"
        }`}
        src={image.trace}
        alt={alt}
      />

      <img
        className={`w-full ${imageLoaded ? " block" : " hidden"}`}
        src={image.src}
        alt={alt}
        style={styles.opacity}
        onLoad={() => setImageLoaded(true)}
      />
    </div>
  );
};

export default ImageUtil;
