import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const ImageLoaderUtil = ({ alt: alternative, src: source }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const { src, trace } = require(`../../content/assets/${source}?trace`);

  return (
    <p className="relative">
      <a className="cursor-pointer" href={src} target="blank">
        <AnimatePresence>
          <motion.img
            key={src + Math.random()}
            className="w-full top-0 left-0 absolute"
            src={src}
            initial={{ opacity: 0 }}
            animate={{ opacity: imageLoaded ? 1 : 0 }}
            alt={alternative}
            loading="lazy"
            transition={{ ease: "easeIn" }}
            onLoad={() => setImageLoaded(true)}
          />
          <motion.img
            key={src}
            className="w-full"
            src={trace}
            alt={alternative}
            initial={{ opacity: 1 }}
            animate={{ opacity: imageLoaded ? 0 : 1 }}
            transition={{ ease: "easeOut" }}
          />
        </AnimatePresence>
      </a>
    </p>
  );
};

export default ImageLoaderUtil;
