import { useState } from "react";

const ImageLoaderUtil = ({ alt, src }) => {
  const image = require(`../../content/assets/${src}?trace`);
  const [imageLoaded, setImageLoaded] = useState(false);

  const visibility = {
    trace: "block",
    src: "hidden",
  };

  if (imageLoaded) {
    visibility.trace = "hidden";
    visibility.src = "block";
  }

  return (
    <>
      <img
        className={`w-full block ${visibility.src}`}
        src={image.src}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      />
      <img
        className={`w-full block ${visibility.trace}`}
        src={image.trace}
        alt={alt}
      />
    </>
  );
};

export default ImageLoaderUtil;
