import { motion, AnimatePresence } from "framer-motion";
import "../styles/global.css";
const pageVariants = {
  initial: {
    opacity: 0,
  },
  in: {
    opacity: 1,
  },
  out: {
    opacity: 0,
  },
};

export default function App({ Component, pageProps }) {
  return (
    <AnimatePresence>
      <motion.div initial="initial" animate="in" exit="out" variants={variants}>
        <Component {...pageProps} />;
      </motion.div>
    </AnimatePresence>
  );
}
