  return (
    <div className="relative">
      <img
        className="absolute top-0 left-0 z-10 w-full transition-all duration-500 ease-in"
        src={image}
        alt={alt}
      />

      {/* <img
        className="w-full"
        src={require(`../../content/assets/${src}`)}
        alt={alt}
        onLoad={() => setImageLoaded(true)}
      /> */}
    </div>
  );
};

export default ImageUtil;
